import {join, basename} from 'path';

import {outputFile, existsSync, statSync, readdir} from 'fs-extra';

import Component from './Component';


/**
 * Bundle components to JS modules (or HTML files)
 *
 * @param {string}  path   - Source directory
 * @param {boolean} [HTML] - Whether bundle as HTML
 *
 * @return {string[]} Component paths
 */
export  async function bundle(path, HTML) {

    const type = HTML ? 'HTML' : 'JS';

    var _type_ = type.toLowerCase(), result = [ ];

    if (existsSync( join(path, `index.${_type_}`) )) {

        const component = new Component( path );

        result[0] = `dist/${component.name}.${_type_}`;

        await outputFile(result[0],  await component[`to${type}`]());
    }

    if (statSync( path ).isDirectory())
        result = result.concat(... await Promise.all(
            (await readdir( path )).map(file  =>  bundle(join(path, file), HTML))
        ));

    return result;
}


/**
 * Bundle components into a JS or HTML package
 *
 * @param {string}  path   - Source directory
 * @param {boolean} [HTML] - Whether bundle as HTML
 *
 * @return {string[]} Component paths
 */
export  async function pack(path, HTML) {

    const file = await bundle(path, HTML);

    await outputFile(
        `dist/index.${HTML ? 'html' : 'js'}`,
        file.map(item => {

            item = basename( item );

            console.info(`√ Component "${item}" is packed in`);

            return  HTML ?
                `<link rel="import" href="${item}">`  :
                `export * from './${item}';`;
        }).join('\n')
    );

    return file;
}

import {readFile, readdir, statSync, outputFile, remove} from 'fs-extra';

import {join, basename, dirname, extname} from 'path';

import { JSDOM } from 'jsdom';

import Package from 'amd-bundle';

import * as SASS from 'sass';

import * as LESS from 'less';

import Stylus from 'stylus';

import promisify from 'promisify-node';

const renderSASS = promisify( SASS.render ),
    renderStylus = promisify( Stylus.render ),
    document = (new JSDOM()).window.document;



export default  class Component {
    /**
     * @param {string} path - Component directory
     */
    constructor(path) {

        this.path = path;

        this.name = basename( path );

        this.entry = join(path, 'index');
    }

    /**
     * @param {string} path - File path
     *
     * @return {string} File content
     */
    static async loadFile(path) {

        return  path.includes('\n')  ?  path  :  ((await readFile( path )) + '');
    }

    static async parseHTML(path) {

        return  JSDOM.fragment(await Component.loadFile( path ));
    }

    static async parseSASS(path) {

        return  renderSASS({data:  await Component.loadFile( path )});
    }

    static async parseLESS(path) {

        return  await LESS.render(await Component.loadFile( path ));
    }

    static async parseStylus(path) {

        return  await renderStylus(await Component.loadFile( path ));
    }

    /**
     * @param {string} source - File path or Style source code
     * @param {string} [type] - MIME type
     *
     * @return {?Element} Style element
     */
    static async parseCSS(source, type) {

        var style;

        type = type  ?  type.split('/')[1]  :  extname( source ).slice(1);

        switch ( type ) {
            case 'css':       style = await Component.loadFile( source );   break;
            case 'sass':
            case 'scss':      style = await Component.parseSASS( source );  break;
            case 'less':      style = await Component.parseLESS( source );  break;
            case 'stylus':    style = await Component.parseStylus( source );
        }

        return  style && Object.assign(
            document.createElement('style'),  {textContent: style}
        );
    }

    /**
     * @param {DocumentFragment} fragment
     *
     * @return {Element[]}
     */
    static findStyle(fragment) {

        return [
            ... fragment.querySelectorAll('link[rel="stylesheet"]'),
            ... [ ].concat(... Array.from(
                fragment.querySelectorAll('template'),
                template  =>  [... template.content.querySelectorAll('style')]
            ))
        ];
    }

    /**
     * @param {string} tagName
     *
     * @return {string}
     */
    static identifierOf(tagName) {

        return  tagName[0].toUpperCase() +
            tagName.replace(/-(\w)/g,  (_, char) => char.toUpperCase()).slice(1);
    }

    /**
     * @param {string} path
     *
     * @return {Element}
     */
    static parseJS(path) {

        path = path.split('.').slice(0, -1).join('.');

        return  Object.assign(document.createElement('script'), {
            text:  `\n${
                (new Package( path )).bundle(
                    this.identifierOf( basename( dirname( path ) ) )
                )}\n`
        });
    }

    /**
     * @param {DocumentFragment} fragment
     *
     * @return {string}
     */
    static stringOf(fragment) {

        return Array.from(
            fragment.childNodes,
            node  =>  node[(node.nodeType === 1) ? 'outerHTML' : 'nodeValue']
        ).join('');
    }

    /**
     * @return {DocumentFragment} HTML version bundle of this component
     */
    async toHTML() {

        const fragment = await Component.parseHTML(this.entry + '.html'),
            CSS = [ ];

        for (let sheet  of  Component.findStyle( fragment )) {

            let style = await Component.parseCSS(
                sheet.textContent  ||  join(this.path, sheet.getAttribute('href')),
                sheet.type
            );

            if (! style)  continue;

            sheet.replaceWith( style );

            if (style.parentNode === fragment)  CSS.push( style );
        }

        fragment.querySelector('template').content.prepend(... CSS);

        const script = fragment.querySelector('script');

        if ( script )
            script.replaceWith(
                Component.parseJS( join(this.path, script.getAttribute('src')) )
            );

        return fragment;
    }

    /**
     * @return {string} JS version bundle of this component
     */
    async toJS() {

        const temp_file = [ ];

        for (let file  of  await readdir( this.path )) {

            file = join(this.path, file);

            if (! statSync( file ).isFile())  continue;

            let type = file.split('.').slice(-1)[0], temp = `${file}.js`;

            switch ( type ) {
                case 'html':
                    file = JSON.stringify(
                        Component.stringOf(await this.toHTML())
                    );
                    break;
                case 'js':
                    continue;
                case 'json':
                    file = await Component.loadFile( file );    break;
                default:
                    file = JSON.stringify(
                        (await Component.parseCSS( file )).textContent
                    );
            }

            temp_file.push( temp );

            await outputFile(temp,  `export default ${file}`);
        }

        const source = (new Package( this.entry )).bundle(
            Component.identifierOf( this.name )
        );

        await Promise.all( temp_file.map(file => remove( file )) );

        return source;
    }
}

import {readFileSync} from 'fs';

import {pack} from './command';

import Commander from 'commander';

import PuppeteerBrowser from 'puppeteer-browser';



const manifest = JSON.parse(readFileSync('package.json') + '');

const folder = manifest.directories || '';


Commander
    .command(
        'pack',
        'Bundle components to a package with JS modules (or HTML files) in it'
    )
    .on('command:pack',  () => pack(folder.lib, Commander.HTML))
    .command('preview',  'Real-time preview during development')
    .on('command:preview',  async () => {

        const command = () => pack(folder.lib, Commander.HTML);

        await command();

        await PuppeteerBrowser.getPage('.',  folder.test || 'test/',  command);
    })
    .option('-H, --HTML',  'Bundle as HTML')
    .parse( process.argv );

