import LESS from 'less';

import * as SASS from 'sass';

import Stylus from 'stylus';

import YAML from 'yaml';


/**
 * @private
 *
 * @param {String} raw
 *
 * @return {String}
 */
export function css(raw) {  return raw;  }


/**
 * @private
 *
 * @param {String} raw
 * @param {String} path
 * @param {Object} [option] - http://lesscss.org/usage/#less-options
 *
 * @return {String}
 */
export async function less(raw, path, option) {

    return  (await LESS.render(raw,  { paths: [path], ...option })).css;
}


/**
 * @private
 *
 * @param {String} raw
 * @param {String} path
 * @param {Object} [option] - https://github.com/sass/node-sass#options
 *
 * @return {String}
 */
export function sass(raw, path, option) {

    return  SASS.renderSync({ data: raw,  includePaths: [path],  ... option }).css;
}

/**
 * @private
 *
 * @type {Function}
 */
export const scss = sass;


/**
 * @private
 *
 * @param {string} raw
 * @param {String} path
 * @param {Object} [option] - https://github.com/stylus/stylus/blob/HEAD/docs/js.md
 *
 * @return {Promise<string>} CSS source code
 */
export function stylus(raw,  path,  option = { }) {

    return  new Promise((resolve, reject) => Stylus.render(
        raw,
        { paths: [path], ...option },
        (error, CSS)  =>  error ? reject( error ) : resolve( CSS )
    ));
}


/**
 * @private
 *
 * @param {String} raw
 * @param {Object} [option] - https://eemeli.org/yaml/#options
 *
 * @return {String}
 */
export function yaml(raw, option) {  return  YAML.parse(raw, option);  }

import { prettify } from '@tech_query/node-toolkit';

import { identifierOf } from './utility';


/**
 * @private
 *
 * @return {String}
 */
export function index_html() {

    return `<template>
    <style>
        h1 {
            color: lightblue;
        }
    </style>

    <h1>Hello, WebCell !</h1>
</template>`;
}


/**
 * @private
 *
 * @param {String}   name
 * @param {String[]} [keys=[]]
 *
 * @return {String}
 */
export function index_js(name,  keys = [ ]) {

    return prettify(`
import { component${keys[0] ? ', mapProperty, mapData' : ''} } from 'web-cell';

import template from './index.html';


@component({ template })
export default  class ${identifierOf(name, true)} extends HTMLElement {

    constructor() {  super().construct();  }
${
    keys[0] ? `
    @mapProperty
    static get observedAttributes() {

        return  ${JSON.stringify( keys )};
    }

    @mapData
    attributeChangedCallback() { }` : ''}
}`);
}


/**
 * @private
 *
 * @param {String}   name
 * @param {String[]} page
 *
 * @return {String}
 */
export  function router_js(name, page) {

    return prettify(`import { component } from 'web-cell';

import HTMLRouter, { load } from 'cell-router';


@component()
export default  class ${identifierOf(name, true)}Router extends HTMLRouter {
${page.map(name => {

        name = name.toLowerCase();

        return `
    @load('/${name}')
    ${name}Page() {  return '<page-${name} />';  }
`;}).join('\n')}
}`);
}

import { cache, packageOf } from '@tech_query/node-toolkit';

import { JSDOM } from 'jsdom';

import { join } from 'path';

import { outputFile } from 'fs-extra';


/**
 * @param {String}   raw
 * @param {?Boolean} big - Big-camel style
 *
 * @return {String}
 */
export function identifierOf(raw, big) {

    raw = raw.replace(/\W+(\w)/g,  (_, char) => char.toUpperCase())
        .replace(/^\d/,  char => '_' + char);

    return  big  ?  (raw[0].toUpperCase() + raw.slice(1))  :  raw;
}


/**
 * Get `package.json` data of `path` or `process.cwd()`
 *
 * @type {function(path: ?String): Object}
 */
export const metaOf = cache(
    path  =>  (packageOf(path || './test') || '').meta || { }
);


/**
 * Get `directories` field of `package.json` in `path` or `process.cwd()`
 *
 * @type {function(path: ?String): Object}
 */
export const folderOf = cache(path  =>  (metaOf(path) || '').directories || { });


/**
 * @param {String} entry          - Path of HTML file
 * @param {String} name           - Tag name
 * @param {String} [base='dist/'] - Base path of JS files
 */
export async function addComponent(entry, name, base) {

    const page = await JSDOM.fromFile( entry );

    const { document } = page.window;

    const script = document.createElement('script');

    script.setAttribute('src',  join(base || 'dist', `${name}.js`));

    document.head.append( script );

    document.body.append( document.createElement( name ) );

    await outputFile(entry, page.serialize());
}

